import { HttpClient,HttpHeaders } from '@angular/common/http';
import { Component, EventEmitter, OnInit, Output, inject } from '@angular/core';
import { Task } from 'src/Models/Task';
import{map} from 'rxjs/operators';
import { TaskService } from '../Services/task.service';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit{
  showCreateTaskForm: boolean = false;
  http:HttpClient=inject(HttpClient);
  allTasks:Task[]=[];
  taskService:TaskService=inject(TaskService);

  editMode:boolean=false;

  selectedTask:Task;




  ngOnInit(){
    this.fetchAllTasks();

  }
  

  OpenCreateTaskForm(){
    this.showCreateTaskForm = true;
    this.editMode=false;
    this.selectedTask={
      title:'',
      desc:'',
      assignedTo:'',
      createdAt:'',
      priority:'',
      status:''


    }
  }

  CloseCreateTaskForm(){
    this.showCreateTaskForm = false;
  }
  CreateOrUpdateTask(data:Task){
    
     this.taskService.CreateTask(data);
    // const headers=new HttpHeaders({'my-header':'hello-world'})
  //  this.http.post<{name:string}>('https://angular-http-client-360ac-default-rtdb.firebaseio.com/tasks.json',data)
  //  .subscribe((response)=>{
  //   console.log(response);
  //   this.fetchAllTasks();
  //  });

   

  } 

  FetchAllTaskClicked(){
    this.fetchAllTasks();

  }

  private fetchAllTasks(){
    // this.http.get<{[key:string]:Task}>('https://angular-http-client-360ac-default-rtdb.firebaseio.com/tasks.json')
    // .pipe(map((response)=>{

    //   //trasforming data
    //   let tasks=[];

    //   for(let key in response){
    //     if(response.hasOwnProperty(key)){
    //       tasks.push({...response[key],id:key});

    //     }
       
    //   }

    //   return tasks;

    // }))
    this.taskService.GetAlltasks()
    .subscribe((tasks)=>{
      this.allTasks=tasks;
      // console.log(tasks);
    });

  }

  DeleteTask(id:string | undefined){
    // this.http.delete('https://angular-http-client-360ac-default-rtdb.firebaseio.com/tasks/'+ id +'.json')
    // .subscribe((res)=>{
    //   this.fetchAllTasks();
    // })
    this.taskService.DeleteTask(id);

  }

  DeleteAllTasks(){
    // this.http.delete('https://angular-http-client-360ac-default-rtdb.firebaseio.com/tasks.json')
    // .subscribe((res)=>{
    //    this.fetchAllTasks();

    // })
    this.taskService.DeleteAllTask();
  }
  
  OnEditTaskClicked(id:string |undefined){
    //open edit task form
    this.showCreateTaskForm=true;
    this.editMode=true; 

   this.selectedTask= this.allTasks.find((task)=>{
      return task.id === id;

    })


  }
}
<app-create-task 
        *ngIf="showCreateTaskForm" 
        (CloseForm)="CloseCreateTaskForm()"
        (EmitTaskData)="CreateOrUpdateTask($event)"
        [isEditMode]="editMode"
        [selectedTask]="selectedTask"

></app-create-task>

<div class="dashboard-container">
    <div style="display: flex;">
        <div style="width: 50%">
            <h2>Dashboard</h2>
        </div>
        <div style="width: 50%; text-align: right;">
            <button class="create-task-btn" (click)="OpenCreateTaskForm()">Create Task</button>
            <button class="create-task-btn" (click)="FetchAllTaskClicked()">Fetch Tasks</button>
            <button class="create-task-btn" (click)="DeleteAllTasks()">Clear Tasks</button>
        </div>
    </div>

    <hr>

    <div class="dashboard-item-container">
        <div style="text-align:center;" *ngIf="allTasks.length < 1">
            <h3>No task has been created yet!</h3>
        </div>
            <div class="task-item" [ngClass]="'task-' + task.status"  *ngFor="let task of allTasks">
                <div class="task-title">
                    <h3>{{ task.title }}</h3>
                </div>
                <p><b>Assigned to: </b>{{task.assignedTo}}</p>
                <p><b>Priority: </b>{{task.priority}}</p>
                <p><b>Status: </b>{{task.status}}</p>
                <div class="task-action-icons">
                    <button class="task-action-btn" title="Detail">
                        <i class="fa fa-info-circle" aria-hidden="true"></i>
                    </button>
                    <button class="task-action-btn" title="Edit" (click)="OnEditTaskClicked(task.id)">
                        <i class="fa fa-pencil-square-o" aria-hidden="true"></i>
                    </button>
                    <button class="task-action-btn" title="Delete" (click)="DeleteTask(task.id)">
                        <i class="fa fa-trash" aria-hidden="true"></i>
                    </button>            
                </div>
            </div>
    </div>
</div>
