import {Injectable,inject} from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Task } from 'src/Models/Task';
import {map} from 'rxjs/operators';
import {Subject} from 'rxjs';

@Injectable({
    providedIn:'root'
})

export class TaskService{

    http:HttpClient=inject(HttpClient);
    errorSubject=new Subject();

    CreateTask(task:Task){
        this.http.post<{name:string}>('https://angular-http-client-360ac-default-rtdb.firebaseio.com/tasks.json',task)
   .subscribe((error)=>{
    this.errorSubject.next();

   }
    // this.fetchAllTasks();
    );

}

    DeleteTask(id:string |undefined){
        this.http.delete('https://angular-http-client-360ac-default-rtdb.firebaseio.com/tasks/'+ id +'.json')
    .subscribe({error:(res)=>{
    //   this.fetchAllTasks();
    
    }});



}

    DeleteAllTask(){
        this.http.delete('https://angular-http-client-360ac-default-rtdb.firebaseio.com/tasks.json')
        .subscribe();
        //    this.fetchAllTasks();

    }

    GetAlltasks(){
       return this.http.get<{[key:string]:Task}>('https://angular-http-client-360ac-default-rtdb.firebaseio.com/tasks.json')
    .pipe(map((response)=>{

      //trasforming data
      let tasks=[];

      for(let key in response){
        if(response.hasOwnProperty(key)){
          tasks.push({...response[key],id:key});

        }
       
      }

      return tasks;

    }))
    
    }
    UpdateTask(id: string | undefined,data:Task){
        this.http.put('https://angular-http-client-360ac-default-rtdb.firebaseio.com/tasks'+id+'.json',data)
        .subscribe();

    }
    

}
